import React, { Component } from 'react';
import { TransitionGroup, CSSTransition, Transition } from 'react-transition-group';
import {
    Switch,
    Route
} from 'react-router-dom';
import Nav from './components/Nav.jsx';
import Home from './components/Home.jsx';
import Projects from './components/Projects.jsx';
import Contact from './components/Contact.jsx';
import Footer from './components/Footer.jsx'
import './App.css';
import Backgrounds from './json/backgrounds.json';

let backgroundIndex = Math.floor(Math.random(0, 1) * Backgrounds.length)
let backgroundObj = Backgrounds[backgroundIndex];
let backgroundURL = backgroundObj.url;

let styles = {
    backgroundImage: 'url(' + backgroundURL + ')',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    backgroundAttachment: 'fixed',
    backgroundSize: 'cover',
    height: '100%',
    verticalAlign: 'top',
    marginLeft: '0',
    marginRight: '0',
    marginTop: '0',
    marginBottom: '-22px',
    minHeight: "100vh"
}

let headercolors = {
    "color": Backgrounds[backgroundIndex].titleFontColor
}

function App() {
    return (
        <div className="App" style={styles}>
            <div className="mainContainer">
                <div className="nav">
                    <Nav data={headercolors} />
                </div>
                <Switch>
                    <Route exact path="/">{({ match }) => (
                        <CSSTransition
                            in={match != null}
                            timeout={3000}
                            classNames="page"
                            unmountOnExit>
                            <div>
                                <Home />
                            </div>
                        </CSSTransition>
                    )}</Route>
                    <Route exact path="/projects">{({ match }) => (
                        <CSSTransition
                            in={match != null}
                            timeout={3000}
                            classNames="page"
                            unmountOnExit>
                            <div>
                                <Projects />
                            </div>
                        </CSSTransition>
                    )}</Route>
                    <Route exact path="/contact">{({ match }) => (
                        <CSSTransition
                            in={match != null}
                            timeout={3000}
                            classNames="page"
                            unmountOnExit>
                            <div>
                                <Contact />
                            </div>
                        </CSSTransition>
                    )}</Route>
                </Switch>
            </div>
            <Footer data={backgroundObj} />
        </div>
    )
}

export default App;
