import React, { useState } from 'react';
import Header from './Header.jsx'
import {
  NavLink,
  useLocation
} from 'react-router-dom';
import './Nav.css';

function Nav(props) {
  const [projectsOpen, updateState] = useState(false)
  let headerColor = props.data.color;
  let location = useLocation().pathname;
  let headerText;

  console.log(props.data.color)

  if (location === '/') {
    headerText = 'james ming johnson';
  } else if (location === '/projects') {
    headerText = 'projects';
  } else if (location === '/contact') {
    headerText = 'contact';
  }

  if (projectsOpen) {
    return (
      <div className="header">
        <Header title={headerText} data={headerColor}/>
        <nav>
          <ul className="nav">
            <li><NavLink to="/" onClick={() => updateState(false)}>home</NavLink></li>
            <li><NavLink to="/projects" activeClassName="selected" exact={true} onClick={() => updateState(true)}>projects</NavLink></li>
            <ul>
              <li><NavLink to="/projects?personal" activeClassName="selected" exact={true}>personal</NavLink></li>
              <li><NavLink to="/projects?professional" activeClassName="selected" exact={true}>professional</NavLink></li>
              <li><NavLink to='/projects?all' activeClassName="selected" exact={true}>all projects</NavLink></li>
            </ul>
            <li><NavLink to="/contact" exact={true} activeClassName="selected" onClick={() => updateState(false)}>contact</NavLink></li>
          </ul>
        </nav>
      </div>
    )
  } else {
    return (
      <div className="header">
        <Header title={headerText} data={headerColor}/>
        <nav>
          <ul>
            <li><NavLink to="/" activeClassName="selected" onClick={() => updateState(false)}>home</NavLink></li>
            <li><NavLink to="/projects" activeClassName="selected" onClick={() => updateState(true)}>projects</NavLink></li>
            <li><NavLink to="/contact" activeClassName="selected" onClick={() => updateState(false)}>contact</NavLink></li>
          </ul>
        </nav>
      </div>
    )
  }
}

export default Nav;
